    private static ByteBuffer loadIcon(String data) {
        int len = data.length();
        ByteBuffer bb = BufferUtils.createByteBuffer(len);
        for (int i = 0; i < len; ++i) {
            bb.put(i, (byte)data.charAt(i));
        }
        return bb.asReadOnlyBuffer();
    }

