    public static boolean isMacOSXEqualsOrBetterThan(int major_required, int minor_required) {
        int minor;
        int major;
        String os_version = LWJGLUtil.getPrivilegedProperty("os.version");
        StringTokenizer version_tokenizer = new StringTokenizer(os_version, ".");
        try {
            String major_str = version_tokenizer.nextToken();
            String minor_str = version_tokenizer.nextToken();
            major = Integer.parseInt(major_str);
            minor = Integer.parseInt(minor_str);
        } catch (Exception e) {
            LWJGLUtil.log("Exception occurred while trying to determine OS version: " + e);
            return false;
        }
        return major > major_required || major == major_required && minor >= minor_required;
    }

    public static Map<Integer, String> getClassTokens(TokenFilter filter, Map<Integer, String> target, Class ... tokenClasses) {
        return LWJGLUtil.getClassTokens(filter, target, Arrays.asList(tokenClasses));
    }

    public static Map<Integer, String> getClassTokens(TokenFilter filter, Map<Integer, String> target, Iterable<Class> tokenClasses) {
        if (target == null) {
            target = new HashMap<Integer, String>();
        }
        int TOKEN_MODIFIERS = 25;
        for (Class tokenClass : tokenClasses) {
            for (Field field : tokenClass.getDeclaredFields()) {
                if ((field.getModifiers() & 0x19) != 25 || field.getType() != Integer.TYPE) continue;
                try {
                    int value = field.getInt(null);
                    if (filter != null && !filter.accept(field, value)) continue;
                    if (target.containsKey(value)) {
                        target.put(value, LWJGLUtil.toHexString(value));
                        continue;
                    }
                    target.put(value, field.getName());
                } catch (IllegalAccessException e) {
                    // empty catch block
                }
            }
        }
        return target;
    }

    public static String toHexString(int value) {
        return "0x" + Integer.toHexString(value).toUpperCase();
    }

    static {
        String osName = LWJGLUtil.getPrivilegedProperty("os.name");
        if (osName.startsWith("Windows")) {
            PLATFORM = 3;
        } else if (osName.startsWith("Linux") || osName.startsWith("FreeBSD") || osName.startsWith("OpenBSD") || osName.startsWith("SunOS") || osName.startsWith("Unix")) {
            PLATFORM = 1;
        } else if (osName.startsWith("Mac OS X") || osName.startsWith("Darwin")) {
            PLATFORM = 2;
        } else {
            throw new LinkageError("Unknown platform: " + osName);
        }
    }

    public static interface TokenFilter {
        public boolean accept(Field var1, int var2);
    }
